function transformURL() {
  var did = (typeof parameters !== 'undefined' && parameters && parameters.disputeId != null)
    ? parameters.disputeId
    : '{{parameters.disputeId}}';

  var disputeId = did;

  if (disputeId && typeof disputeId === 'object') {
    disputeId = disputeId.value || disputeId.id || disputeId.disputeId || (disputeId.data && (disputeId.data.value || disputeId.data.id || disputeId.data.disputeId));
  }

  if (typeof disputeId === 'string') {
    var raw = disputeId.trim().replace(/^"|"$/g, '');
    if ((raw[0] === '{' && raw[raw.length - 1] === '}') || (raw[0] === '[' && raw[raw.length - 1] === ']')) {
      try {
        var obj = JSON.parse(raw);
        if (Array.isArray(obj)) obj = obj[0];
        disputeId = obj && typeof obj === 'object'
          ? obj.value || obj.id || obj.disputeId || (obj.data && (obj.data.value || obj.data.id || obj.data.disputeId)) || raw
          : raw;
      } catch (e) {
        disputeId = raw;
      }
    } else {
      disputeId = raw;
    }
  }

  if (!disputeId || disputeId === '[object Object]') throw new Error('Invalid disputeId');

  return (url.endsWith('/') ? url : url + '/') + encodeURIComponent(String(disputeId));
}
return transformURL();
