function transformURL() {
  var pid = (typeof parameters !== 'undefined' && parameters && parameters.paymentId != null)
    ? parameters.paymentId
    : '{{parameters.paymentId}}';

  var paymentId = pid;

  if (paymentId && typeof paymentId === 'object') {
    paymentId = paymentId.value || paymentId.id || paymentId.paymentId || (paymentId.data && (paymentId.data.value || paymentId.data.id || paymentId.data.paymentId));
  }

  if (typeof paymentId === 'string') {
    var raw = paymentId.trim().replace(/^"|"$/g, '');
    if ((raw[0] === '{' && raw[raw.length - 1] === '}') || (raw[0] === '[' && raw[raw.length - 1] === ']')) {
      try {
        var obj = JSON.parse(raw);
        if (Array.isArray(obj)) obj = obj[0];
        paymentId = obj && typeof obj === 'object'
          ? obj.value || obj.id || obj.paymentId || (obj.data && (obj.data.value || obj.data.id || obj.data.paymentId)) || raw
          : raw;
      } catch (e) {
        paymentId = raw;
      }
    } else {
      paymentId = raw;
    }
  }

  if (!paymentId || paymentId === '[object Object]') throw new Error('Invalid paymentId');

  return (url.endsWith('/') ? url : url + '/') + encodeURIComponent(String(paymentId));
}
return transformURL();

