function transformURL() {
  var rid = (typeof parameters !== 'undefined' && parameters && parameters.refundId != null)
    ? parameters.refundId
    : '{{parameters.refundId}}';

  var refundId = rid;

  if (refundId && typeof refundId === 'object') {
    refundId = refundId.value || refundId.id || refundId.refundId || (refundId.data && (refundId.data.value || refundId.data.id || refundId.data.refundId));
  }

  if (typeof refundId === 'string') {
    var raw = refundId.trim().replace(/^"|"$/g, '');
    if ((raw[0] === '{' && raw[raw.length - 1] === '}') || (raw[0] === '[' && raw[raw.length - 1] === ']')) {
      try {
        var obj = JSON.parse(raw);
        if (Array.isArray(obj)) obj = obj[0];
        refundId = obj && typeof obj === 'object'
          ? obj.value || obj.id || obj.refundId || (obj.data && (obj.data.value || obj.data.id || obj.data.refundId)) || raw
          : raw;
      } catch (e) {
        refundId = raw;
      }
    } else {
      refundId = raw;
    }
  }

  if (!refundId || refundId === '[object Object]') throw new Error('Invalid refundId');

  return (url.endsWith('/') ? url : url + '/') + encodeURIComponent(String(refundId));
}
return transformURL();
